(30728, 454, '_crb_manufac_banner_sub_text', 'Zetwerk manufactures high-quality extrusions with complex cross-sections using aluminum alloys. It produces extrusions to exact specification and offers necessary secondary operations such as CNC machining and surface treatments.'),
(30729, 454, '_crb_manufac_banner_padding', '2'),
(30730, 454, '_crb_manufac_image', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/banner-1.png'),
(30731, 454, '_crb_manufact_section|||0|value', 'manufac_top_text_btm_img'),
(30732, 454, '_crb_manufact_section|||1|value', 'manufac_service_section'),
(30733, 454, '_crb_manufact_section|||2|value', 'manufac_text_right_img'),
