(30735, 454, '_crb_manufact_section|||4|value', 'manufac_indust_slider'),
(30736, 454, '_crb_manufact_section|manufac_top_text_btm_heading|0|0|value', 'METHODS'),
(30737, 454, '_crb_manufact_section|manufac_top_text_btm_subtext|0|0|value', 'Zetwerk uses industry-leading die casting methods like High Pressure Die Casting (HPDC), Low Pressure Die Casting (LPDC) and Gravity Die Casting (GDC), with capacity ranging from 80T to 3500T. Shaped in a variety of metal alloys like aluminum, zinc, copper, brass and bronze alloys, die cast components are formed in precision steel tools, moulds and dies as per volume, size, weight, geometry, complexity and surface finish requirements.'),
(30738, 454, '_crb_manufact_section|manufac_top_btm_imgs|0|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/methods-img.png'),
(30739, 454, '_crb_manufact_section|manufac_service_heading|1|0|value', 'FINISHING TOUCHES'),
(30740, 454, '_crb_manufact_section|crb_manufac_indust_slider|1|0|value', '_'),
(30741, 454, '_crb_manufact_section|crb_manufac_indust_slider|1|1|value', '_'),
