(39622, 549, '_crb_manufac_banner_title', 'SAND CASTING'),
(39623, 549, '_crb_manufac_banner_sub_text', 'Zetwerks sand casting services are a cost-efficient method for producing small and large parts from various ferrous and non-ferrous alloys. The process is calibrated to produce large scale workpieces in high volumes and for short run work.'),
(39624, 549, '_crb_manufac_banner_padding', '2'),
(39625, 549, '_crb_manufac_image', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/sand-cast.png'),
