(52372, 455, '_crb_manufac_banner_title', 'DIE CASTING'),
(52373, 455, '_crb_manufac_banner_sub_text', 'Zetwerk manufactures high-quality extrusions with complex cross-sections using aluminum alloys. It produces extrusions to exact specification and offers necessary secondary operations such as CNC machining and surface treatments.'),
(52374, 455, '_crb_manufac_banner_padding', '2'),
(52375, 455, '_crb_manufac_image', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/banner-1.png'),
(52376, 455, '_crb_manufact_section|||0|value', 'manufac_top_text_btm_img'),
(52377, 455, '_crb_manufact_section|||1|value', 'manufac_service_section'),
(52378, 455, '_crb_manufact_section|||2|value', 'manufac_text_right_img'),
(52379, 455, '_crb_manufact_section|||3|value', 'manufac_client_logos'),
(52380, 455, '_crb_manufact_section|||4|value', 'manufac_indust_slider'),
(52381, 455, '_crb_manufact_section|manufac_top_text_btm_heading|0|0|value', 'METHODS'),
(52382, 455, '_crb_manufact_section|manufac_top_text_btm_subtext|0|0|value', 'Zetwerk uses industry-leading die casting methods like High Pressure Die Casting (HPDC), Low Pressure Die Casting (LPDC) and Gravity Die Casting (GDC), with capacity ranging from 80T to 3500T. Shaped in a variety of metal alloys like aluminum, zinc, copper, brass and bronze alloys, die cast components are formed in precision steel tools, moulds and dies as per volume, size, weight, geometry, complexity and surface finish requirements.'),
(52383, 455, '_crb_manufact_section|manufac_top_btm_imgs|0|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/methods-img.png'),
(52384, 455, '_crb_manufact_section|manufac_service_heading|1|0|value', 'FINISHING TOUCHES'),
(52385, 455, '_crb_manufact_section|crb_manufac_indust_slider|1|0|value', '_'),
(52386, 455, '_crb_manufact_section|crb_manufac_indust_slider|1|1|value', '_'),
