(52923, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_heading|1:0|0|value', 'Fabric and Trim Sourcing'),
(52924, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_sub_text|1:0|0|value', 'Zetwerk has a dedicated Fabric and Trim sourcing team for knits and wovens for both denims and non-denims. It partners with fabrics mills in India and abroad to develop proprietary CAD designs to create a variety of designs for a single fabric. Zetwerk also works towards sustainability, and has certified sustainable fabric connections.'),
(52925, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_img|1:0|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/primary-finishing.png'),
(52926, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_heading|1:1|0|value', 'Designing'),
(52927, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_sub_text|1:1|0|value', 'Zetwerks expert team of designers works with global fashion brands to co-create state-of-the-art finished products to transform clients creative requirements into reality. Its CLO 3D software-enabled process serves as a one stop solution from design to delivery.'),
(52928, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_img|1:1|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/primary-finishing.png'),
(52929, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_heading|1:2|0|value', 'Production and Sampling'),
(52930, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_sub_text|1:2|0|value', 'Zetwerk engages in high-quality production of a wide variety of garments, from football jerseys to cold-weather military uniforms, loungewear to designer and luxury clothing. Zetwerk currently operates manufacturing partner facilities across India and caters to national as well as international customers.'),
(52931, 693, '_crb_manufact_section|crb_manufac_indust_slider:manufac_service_section_img|1:2|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/primary-finishing.png'),
