(54960, 488, '_crb_manufact_section|manufac_top_text_btm_heading|0|0|value', 'METHODS'),
(54961, 488, '_crb_manufact_section|manufac_top_text_btm_subtext|0|0|value', 'Benchmark techniques of Gravity Fed and Vacuum casting are used to produce parts ranging from a few grams to 30 kgs in weight. For investment casting, Zetwerk works with carbon steel, low alloy steel, stainless steel grades, and various super alloys, along with non-ferrous metals, such as aluminum, brass, bronze and copper.'),
(54962, 488, '_crb_manufact_section|manufac_top_btm_imgs|0|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/methods-img.png'),
