(54966, 488, '_crb_manufact_section|crb_manufac_indust_slider:manufac_tobi_section_heading|1:0|0|value', 'PATTERN TOOLING AND WAX PATTERN PROTOTYPING'),
(54967, 488, '_crb_manufact_section|crb_manufac_indust_slider:manufac_tobi_section_sub_text|1:0|0|value', 'A negative of the final metal part is created through a machined aluminum mold into which wax is injected to create a positive of the final metal part. Zetwerk also uses 3D printing to create complex wax patterns for rapid prototyping.'),
(54968, 488, '_crb_manufact_section|crb_manufac_indust_slider:manufac_tobi_section_img|1:0|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/primary-finishing.png'),
(54969, 488, '_crb_manufact_section|crb_manufac_indust_slider:manufac_tobi_section_heading|1:1|0|value', 'FINISHING TOUCHES'),
(54970, 488, '_crb_manufact_section|crb_manufac_indust_slider:manufac_tobi_section_sub_text|1:1|0|value', 'Zetwerk offers secondary operations for finishing machine investment cast components to impart tight dimensional tolerance, heat treatment to achieve mechanical propeservicerties and surface finishing processes to meet engineering specifications. Zetwerk also offers the produced parts in a sub-assembly along with hardware and other components necessary for assembly.'),
(54971, 488, '_crb_manufact_section|crb_manufac_indust_slider:manufac_tobi_section_img|1:1|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/finishing-touch-1.png'),
(54972, 488, '_crb_manufact_section|manufac_tri_sec_heading|2|0|value', 'QUALITY CONTROL AND ASSEMBLY'),
(54973, 488, '_crb_manufact_section|manufac_tri_sec_sub_text|2|0|value', 'Zetwerk offers rigorous casting quality inspection and product testing services to ensure compliance with engineering specifications. Some services include chemical composition testing, tensile strength testing, hardness testing, impact testing, dye-penetrant testing, magnetic particle inspection and radiography inspection.'),
