(58050, 726, '_crb_manufac_banner_title', 'DIE CASTING'),
(58051, 726, '_crb_manufac_banner_sub_text', 'Zetwerk manufactures high-quality extrusions with complex cross-sections using aluminum alloys. It produces extrusions to exact specification and offers necessary secondary operations such as CNC machining and surface treatments.'),
(58052, 726, '_crb_manufac_banner_padding', '2'),
(58053, 726, '_crb_manufac_image', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/banner-1.png'),
(58054, 726, '_crb_manufact_section|||0|value', 'manufac_top_text_btm_img'),
(58055, 726, '_crb_manufact_section|||1|value', 'manufac_service_section'),
(58056, 726, '_crb_manufact_section|||2|value', 'manufac_text_right_img'),
(58057, 726, '_crb_manufact_section|||3|value', 'manufac_client_logos'),
