(72218, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_sub_text|1:0:0|0|value', '<div class=\"modal-header d-block\">\r\n<div class=\"sub-head mt-2\">\r\n<p class=\"c-desc\">A critical hinge mount part, ready for assembly. This small part plays an important role in the hinge arm assembly, for an Antenna Deployment Rig. Zetwerk provides world-class solutions to customers across the Aerospace &amp; Defense sector.</p>\r\n\r\n</div>\r\n</div>'),
(72219, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_image|1:0:0|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/01/Aerospace-Defence-2-scaled.jpg'),
(72220, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_title|1:0:1|0|value', 'State-of-the-art Machinery'),
(72221, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_sub_text|1:0:1|0|value', '<div class=\"modal-header d-block\">\r\n<div class=\"sub-head mt-2\">\r\n<p class=\"c-desc\">Sophisticated machinery at work, at a critical Zetwerk partner facility. Zetwerks software-enabled process, coupled with quality control and manufacturing supervision at each stage, ensures that the best protocols are in place to enable smooth production and faster time to market.</p>\r\n\r\n</div>\r\n</div>'),
