(72224, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_sub_text|1:0:2|0|value', '<div class=\"modal-header d-block\">\r\n<div class=\"sub-head mt-2\">\r\n<p class=\"c-desc\">A glimpse into the hustle and bustle of the shop floor at a key Zetwerk partner facility. With a smooth design-to-delivery-to-warehousing capacity, Zetwerk helps expand its clients product engineering bandwidth, while helping drive value, across fabric classes.</p>\r\n\r\n</div>\r\n</div>\r\n<div class=\"modal-body p-0\"></div>'),
(72225, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_image|1:0:2|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/02/Apparel-4.jpg'),
(72226, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_title|1:0:3|0|value', 'To The Small Screen'),
(72227, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_sub_text|1:0:3|0|value', '<div class=\"modal-header d-block\">\r\n<div class=\"sub-head mt-2\">\r\n<p class=\"c-desc\">LED television production at Zetwerks partner facility in Coimbatore. Spread across ~33,000 square feet, the facility serves as the first TV manufacturing unit of its kind in the state of Tamil Nadu, equipped with state-of-the-art machinery, with a production capacity of 75,000 units per month of smart and non-smart LED television sets.</p>\r\n\r\n</div>\r\n</div>\r\n<div class=\"modal-body p-0\"></div>'),
(72228, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_image|1:0:3|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/02/Consumer-Goods-2-scaled.jpg'),
(72229, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_title|1:0:4|0|value', 'Marine Unloading Arms Installation'),
(72230, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_sub_text|1:0:4|0|value', '<div class=\"modal-header d-block\">\r\n<div class=\"sub-head mt-2\">\r\n<p class=\"c-desc\">A marine unloading arms installation in an LPG terminal as part of a critical LPG import terminal project in Kerala. Post completion, the terminal is expected to save on the daily transportation of 125 LPG trucks across 1000 kilometers.</p>\r\n\r\n</div>\r\n</div>\r\n<div class=\"modal-body p-0\"></div>'),
(72231, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_image|1:0:4|0|value', 'http://localhost:8888/zetwerk/wp-content/uploads/2022/02/oil-gas1.png'),
(72232, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_title|1:0:5|0|value', 'Building the Nations Oil & Gas Assets'),
(72233, 827, '_crb_ma_section|crb_ma_image_complex:crb_ma_image_loop:crb_mai_sub_text|1:0:5|0|value', '<div class=\"modal-header d-block\">\r\n<div class=\"sub-head mt-2\">\r\n<p class=\"c-desc\">The fabrication and erection of above ground utility and process piping in an oil refinery, as part of one of Zetwerks key Oil and Gas projects .</p>\r\n\r\n</div>\r\n</div>\r\n<div class=\"modal-body p-0\"></div>'),
